CREATE TABLE Address (Id int(10) NOT NULL AUTO_INCREMENT, City varchar(255), District varchar(255), Town varchar(255), Description varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE `User` (Id int(10) NOT NULL AUTO_INCREMENT, AccountId int(10) NOT NULL, FullNameId int(10) NOT NULL, AddressId int(10) NOT NULL, PhoneNumber varchar(255), Email varchar(255), Birthday varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE FullName (Id int(10) NOT NULL AUTO_INCREMENT, FirstName varchar(255), LastName varchar(255), MidName varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE Book (Id int(10) NOT NULL AUTO_INCREMENT, CategoryId int(10) NOT NULL, BookDetailId int(10) NOT NULL, Name varchar(255), Author varchar(255), Publisher varchar(255), PublishYear varchar(255), Quantity int(10) NOT NULL, Saleprice float NOT NULL, Purchaseprice float NOT NULL, PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE BookDetail (Id int(10) NOT NULL AUTO_INCREMENT, OrderId int(10) NOT NULL, Price float NOT NULL, Quantity int(10) NOT NULL, PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE Account (Id int(10) NOT NULL AUTO_INCREMENT, Username varchar(255), Password varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE `Order` (Id int(10) NOT NULL AUTO_INCREMENT, ShippingId int(10) NOT NULL, BillId int(10) NOT NULL, UserId int(10) NOT NULL, PriceShipping int(10) NOT NULL, Discount float NOT NULL, PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE Category (Id int(10) NOT NULL AUTO_INCREMENT, Name varchar(255), Note varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE Shipping (Id int(10) NOT NULL AUTO_INCREMENT, Region varchar(255), Cost float NOT NULL, DelayDate varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE Bill (Id int(10) NOT NULL AUTO_INCREMENT, IdOrder int(10) NOT NULL, TypePayment int(10) NOT NULL, StatePayment varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
CREATE TABLE AddressCheckout (Id int(10) NOT NULL AUTO_INCREMENT, OrderId int(10) NOT NULL, City varchar(255), Distric varchar(255), Town varchar(255), Description varchar(255), PRIMARY KEY (Id)) ENGINE=InnoDB CHARACTER SET UTF8;
